SELECT F_USR_INFO(B.LEND_BOKG_PEN_CNB,'2') AS F_USR_INFO,
    B.LEND_BOKG_APL_DT,
    F_DPT_INFO(F_USR_INFO(B.LEND_BOKG_PEN_CNB,'5'), '1') AS F_DPT_INFO
FROM TBCSPQBO001M A, TBCSPQBO003H B WHERE A.COPT_CAT_CD = B.COPT_CAT_CD AND A.BOO_KND_CD = B.BOO_KND_CD AND A.BOO_NO = B.BOO_NO AND A.CAT_SYM_NM = B.CAT_SYM_NM AND A.BOO_VLE_NO = B.BOO_VLE_NO AND A.BOO_KND_CD = #strBOO_KND_CD# AND A.BOO_NO = #strBOO_NO# AND A.CAT_SYM_NM = #strCAT_SYM_NM# AND A.COPT_CAT_CD = #strCOPT_CAT_CD# AND A.BOO_VLE_NO = #strBOO_VLE_NO# AND LEND_BOKG_STA_CD = 'Y' ORDER BY BOO_BOKG_SLN ASC