SELECT T1.PBC_YR AS PBC_YR /* 발간년도 */,
    T1.PBC_NO AS PBC_NO /* 발간번호 */,
    T1.PBC_YR || '-' || T1.PBC_NO AS PBC_YR /* 발간연번호 */,
    T1.PBC_KND_CD AS PBC_KND_CD /* 발간종류코드 */,
    F_CODE_NM('1000903', T1.PBC_KND_CD) AS F_CODE_NM /* 발간종류명 */,
    T1.PBC_TIT_TXT AS PBC_TIT_TXT /* 발간제목내용 */,
    T1.NTFCTN_RCPT_YMD AS NTFCTN_RCPT_YMD /* 통보접수일자 */,
    T1.PBC_DT AS PBC_DT /* 발간일 */,
    (SELECT COUNT(1)
FROM TBCSPRGA002L WHERE PBC_YR = T1.PBC_YR AND PBC_NO = T1.PBC_NO) AS PBC_RQR_CNT /* 발간요구건수 */ ,T1.RQSTR_DEPT_CD AS RQSTR_DEPT_CD /* 요청부서코드 */ ,F_DPT_INFO(T1.RQSTR_DEPT_CD, '1') AS RQR_DPT_NM /* 요청부서명 */ ,T1.RQSTR_ENO AS RQSTR_ENO /* 요청전산번호 */ ,F_USR_INFO(T1.RQSTR_ENO, '2') AS RQR_NM /* 요청자명 */ ,T1.CRU_PRCS_STTS_SECD AS CRU_PRCS_STTS_SECD /* 처리상태코드 */ ,F_CODE_NM('1000907', T1.CRU_PRCS_STTS_SECD) AS PRCS_STTS_NM /* 처리상태명 */ ,T1.ATRZ_STTS_SECD AS ATRZ_STTS_SECD /* 결재상태코드 */ ,F_CODE_NM('1000333', T1.ATRZ_STTS_SECD) AS APV_STA_NM /* 결재상태명 */ ,T1.ATRZ_DOC_NO AS ATRZ_DOC_NO /* 결재문서번호 */ ,T1.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 문서ID */ FROM TBCSPRGA001M T1 WHERE 1=1 AND T1.NTFCTN_RCPT_YMD BETWEEN #strAcpDtStr# AND #strAcpDtEnd# AND T1.RQSTR_ENO = #RQSTR_ENO# AND T1.PBC_KND_CD = #strPbcKndCd# AND T1.PBC_TIT_TXT LIKE '%' || #strPbcTitTxt# || '%'