SELECT C.CRS_CDN /* 과정기수 */,
    B.CRS_NM /* 과정명 */,
    A.EDU_GL_TXT,
    A.EDU_TG_TXT || ' ' || C.TPOF_NOPE || '명' AS EDU_TG_TXT,
    SUBSTR(C.EDU_BGNG_YMD, 0, 4) || '. ' || SUBSTR(C.EDU_BGNG_YMD, 5, 2) || '. ' || SUBSTR(C.EDU_BGNG_YMD, 7, 2) || ' ~ ' || SUBSTR(C.EDU_END_YMD, 0, 4) || '. ' || SUBSTR(C.EDU_END_YMD, 5, 2) || '. ' || SUBSTR(C.EDU_END_YMD, 7, 2) || '(' || TO_CHAR((TO_DATE(C.EDU_END_YMD, 'YYYYMMDD') - TO_DATE(C.EDU_BGNG_YMD, 'YYYYMMDD'))+1) || '일) ' || E.SEAT_NM || ' ' || E.LCT_RM_NM AS SUBSTR,
    C.CRS_CHIF_NM,
    A.CHR_TNB
FROM TBCSPSAE201M A , TBCSPSAE103C B , TBCSPSAE201D C , TBCSPSAE203D D , TBCSPSAE114C E WHERE B.CRS_CD = A.CRS_CD AND C.LINK_YR = A.LINK_YR AND C.CRS_CD = A.CRS_CD AND D.LINK_YR = C.LINK_YR AND D.CRS_CD = C.CRS_CD AND D.CRS_CDN = C.CRS_CDN AND E.LCT_RM_NO = D.LCT_RM_NO AND D.ASG_YN = 'Y' AND A.LINK_YR = #LINK_YR# AND A.CRS_CD = #strEDU_CRS_CD# AND C.CRS_CDN = #CRS_CDN#