SELECT A.LINK_YR /* 연계년도 */,
    G.CRS_NM /* 과정명 */,
    A.CRS_CDN /* 과정기수 */,
    (SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000312' AND CD = D.PFSR_DVSN_CD) AS PFSR_DVSN_NM , C.BLL_SBJCT_NM , D.PFSR_BLT_NM || '-' || D.INFO_RLPR_NM AS INFO_RLPR_NM , MAX(E.ASG_SNO) AS LCT_TIME , NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) AS BSC_PFSR_FEE , NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) AS EXCS_PFSR_FEE , NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) + NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) AS AB , NVL(B.TREP, 0) AS TREP , NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) + NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) + NVL(B.TREP, 0) AS ABC , NVL(B.TAX_RTE, 0) AS TAX_RTE , (NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) + NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) + NVL(B.TREP, 0)) * NVL(B.TAX_RTE, 0) AS EE , NVL(B.GIVE_AMT, (NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) + NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) + NVL(B.TREP, 0))-((NVL(B.BSC_PFSR_FEE, (1*F.BSC_PFSR_FEE)) + NVL(B.EXCS_PFSR_FEE, (MAX(E.ASG_SNO)-1)*F.EXCS_PFSR_FEE) + NVL(B.TREP, 0)) * NVL(B.TAX_RTE, 0))) AS GIVE_AMT , NVL(B.DEPTS_BNK_NM, D.DEPTS_BNK_NM || ' ' || D.ACTNO_SCRTY) AS DEPTS_BNK_NM FROM TBCSPSAE101D A , TBCSPSAE305D B , TBCSPSAE101M C , TBCSPSAE109M D , TBCSPSAE202D E , (SELECT PFSR_RK_CD,
    BSC_PFSR_FEE,
    EXCS_PFSR_FEE,
    APC_DT
FROM TBCSPSAE112B) F , TBCSPSAE103C G WHERE 1=1 AND A.LINK_YR = B.LINK_YR(+) AND A.CRS_CD = B.CRS_CD(+) AND A.CRS_CDN = B.CRS_CDN(+) AND A.SUBJ_CD = B.SUBJ_CD(+) AND A.PFSR_ID = B.PFSR_ID(+) AND C.SUBJ_CD = A.SUBJ_CD AND D.PFSR_ID = A.PFSR_ID AND E.LINK_YR = A.LINK_YR AND E.CRS_CD = A.CRS_CD AND E.CRS_CDN = A.CRS_CDN AND E.SUBJ_CD = A.SUBJ_CD AND E.PFSR_ID = A.PFSR_ID AND F.PFSR_RK_CD = D.PFSR_RK_CD AND G.CRS_CD = A.CRS_CD AND F.APC_DT = (SELECT MAX(APC_DT) AS APC_DT
FROM TBCSPSAE112B WHERE APC_DT <= E.LCT_DT) AND C.BSC_OP_SUBJ_YN != 'Y' AND A.LINK_YR = #LINK_YR# AND A.CRS_CD = #strEDU_CRS_CD# AND A.CRS_CDN = #CRS_CDN# AND B.PMT_YN = 'Y' GROUP BY A.LINK_YR, G.CRS_NM, A.CRS_CDN, D.PFSR_DVSN_CD, C.BLL_SBJCT_NM, D.PFSR_BLT_NM, D.INFO_RLPR_NM, B.BSC_PFSR_FEE, B.EXCS_PFSR_FEE, B.TREP, B.TAX_RTE, B.GIVE_AMT, B.DEPTS_BNK_NM, D.DEPTS_BNK_NM, D.ACTNO_SCRTY, B.PMT_YN, F.PFSR_RK_CD, F.BSC_PFSR_FEE, A.LCT_TM_HM, A.JON_TM_HM, A.ETC_TM_HM, F.BSC_PFSR_FEE, F.EXCS_PFSR_FEE ORDER BY PFSR_DVSN_CD, INFO_RLPR_NM