SELECT A.PFSR_DVSN_CD,
    (SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000312' AND CD = A.PFSR_DVSN_CD) AS PFSR_DVSN_NM , A.INFO_RLPR_NM , A.PFSR_BLT_NM , A.PFSR_RANK_NM , A.PFSR_TNB , A.PFSR_EML_AD , A.VLD_DVSN_CD , (SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000313' AND CD = A.VLD_DVSN_CD) AS VLD_DVSN_NM , A.PFSR_ID FROM TBCSPSAE109M A WHERE 1=1 AND A.PFSR_DVSN_CD = #strPFSR_DVSN_CD# AND A.INFO_RLPR_NM LIKE '%' || #strNAM# || '%' AND A.VLD_DVSN_CD = #strVLD_DVSN_CD# ORDER BY A.PFSR_DVSN_CD, A.INFO_RLPR_NM, A.PFSR_BLT_NM, A.PFSR_RANK_NM