FROM TBDCMACM013C D3 WHERE D1.VLD_DVSN_CD = D3.CD AND D3.CMM_CD_DVSN_CD = '1000313') AS VLD_DVSN_NM /*유효여부*/ , D1.CVLCPT_DOC_ID , (SELECT ENAIS_ATCH_FILE_NM /* ENAIS첨부파일명 */
FROM ECM_FILE_LIST_VIEW2 D2 WHERE D1.CVLCPT_DOC_ID = D2.CVLCPT_DOC_ID) AS IMG_FILE_NAME FROM TBCSPSAE109M D1 WHERE D1.PFSR_ID = #strPFSR_ID#