SELECT B.INFO_RLPR_NM /* 정보관계자명 */,
    C.EDU_DVSN_NM,
    D.EDU_CAT_NM,
    E.CRS_NM /* 과정명 */,
    A.CRS_CDN /* 과정기수 */,
    SUBSTR(F.EDU_BGNG_YMD, 0, 4)||'.'||SUBSTR(F.EDU_BGNG_YMD, 5, 2)||'.'||SUBSTR(F.EDU_BGNG_YMD, 7, 2)||' ~ '||SUBSTR(F.EDU_END_YMD, 0,4)||'.'||SUBSTR(F.EDU_END_YMD, 5,2)||'.'||SUBSTR(F.EDU_END_YMD, 7,2) AS SUBSTR,
    A.GIVE_AMT /* 지급금액 */
FROM TBCSPSAE305D A , TBCSPSAE109M B , TBCSPSAE101C C , TBCSPSAE102C D , TBCSPSAE103C E , TBCSPSAE201D F WHERE 1=1 AND B.PFSR_ID = A.PFSR_ID AND E.CRS_CD = A.CRS_CD AND D.EDU_CAT_CD = E.EDU_CAT_CD AND D.EDU_DVSN_CD = E.EDU_DVSN_CD AND C.EDU_DVSN_CD = D.EDU_DVSN_CD AND F.LINK_YR = A.LINK_YR AND F.CRS_CD = A.CRS_CD AND F.CRS_CDN = A.CRS_CDN AND A.PMT_YN = 'Y' AND SUBSTR(F.EDU_BGNG_YMD, 0, 6) BETWEEN SUBSTR(#strFromYm#, 0, 6) AND SUBSTR(#strToYm#, 0, 6) AND C.EDU_DVSN_CD = #strEDU_DVSN_CD# AND E.EDU_CAT_CD = #strEDU_CAT_CD# AND A.CRS_CD = #strEDU_CRS_CD# AND A.CRS_CDN = #CRS_CDN# ORDER BY C.EDU_DVSN_NM, D.EDU_CAT_NM, E.CRS_NM, A.CRS_CDN, B.INFO_RLPR_NM