SELECT (CASE WHEN C.CRS_CD = '057' THEN D.EDU_CAT_NM || '(' || C.CRS_NM || ')' ELSE C.CRS_NM END) AS CRS_NM /* 과정명 */,
    B.CRS_CDN /* 과정기수 */,
    SUBSTR(TO_CHAR(SYSDATE, 'YYYYMMDD'), 0, 4) || '. ' || SUBSTR(TO_CHAR(SYSDATE, 'YYYYMMDD'), 5, 2) || '. ' || SUBSTR(TO_CHAR(SYSDATE, 'YYYYMMDD'), 7, 2) AS SUBSTR,
    A.ACTNO_SCRTY /* 계좌번호보안 */,
    (CASE WHEN C.CRS_CD = '063' OR C.CRS_CD = '082' THEN '기본-' WHEN C.CRS_CD = '069' THEN '관리-' WHEN C.CRS_CD = '064' OR C.CRS_CD = '065' OR C.CRS_CD = '066' OR C.CRS_CD = '109' THEN '전문-' WHEN C.CRS_CD = '046' THEN '監事-' WHEN C.CRS_CD = '045' OR C.CRS_CD = '049' OR C.CRS_CD = '054' OR C.CRS_CD = '057' OR C.CRS_CD = '125' OR C.CRS_CD = '126' THEN '제' ELSE '기본-' END) AS CRS_CD
FROM TBCSPSAE201M A , TBCSPSAE201D B , TBCSPSAE103C C , TBCSPSAE102C D WHERE B.LINK_YR = A.LINK_YR AND B.CRS_CD = A.CRS_CD AND C.CRS_CD = A.CRS_CD AND C.EDU_DVSN_CD = D.EDU_DVSN_CD AND C.EDU_CAT_CD = D.EDU_CAT_CD AND A.LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND A.CRS_CD = #strEDU_CRS_CD# AND B.CRS_CDN = #CRS_CDN#