WITH DATA AS ( /* CSPSAE301EExcel4 */ SELECT A.EDU_EXS,
    A.DMT_EXS,
    A.BRKF_FDEX,
    A.LUNCH_FDEX,
    A.DINR_FDEX
FROM TBCSPSAE113B A WHERE A.APC_DT = (SELECT NVL(MAX(B.APC_DT), (SELECT MAX(APC_DT)
FROM TBCSPSAE113B WHERE CRS_CD = '000' AND APC_DT 20250312 감사교육 금요일 점심식비 추가에 따른 수정_천은정감사관*/ , (TO_NUMBER(A.EDU_PRD_TXT)-1) * (SELECT DINR_FDEX
FROM DATA) * (MAX(D.DMT_UE_CNT)) AS TOT_DINR_FDEX , NVL(MAX(F.PAYBACK), 0) AS PAYBACK , NVL(MAX(G.RMRK_CN), '없음') AS RMRK_CN , SUBSTR(TO_CHAR(SYSDATE, 'YYYYMM'), 0, 4) || '. ' || SUBSTR(TO_CHAR(SYSDATE, 'YYYYMM'), 5, 2) AS TODAY , (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = #TRGT_NOP_ENO#) AS USER_NM , (SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE SRECS_DEPT_CD = (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = #TRGT_NOP_ENO#)) AS CNSTN_MBCMT_DEPT_NM , MAX(H.FRIDY_DCN) AS FRIDY_DCN /* 20240613 교육과정 점심식비에 금요일 미포함 계산식 조정으로 추가 */ FROM TBCSPSAE201M A, TBCSPSAE201D B, TBCSPSAE301D C, (SELECT COUNT(TANE_ID) AS TANE_ID
FROM TBCSPSAE301D WHERE LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND CRS_CD = #strEDU_CRS_CD# AND CRS_CDN = #CRS_CDN# AND DMT_UE_YN = 'Y' ) D, (SELECT COUNT(TANE_ID) AS TANE_ID
FROM TBCSPSAE301D WHERE LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND CRS_CD = #strEDU_CRS_CD# AND CRS_CDN = #CRS_CDN# AND SNDY_LODG_YN = 'Y' ) E, (SELECT SUM(UNPAID_AMT) AS UNPAID_AMT /* 미납금액 */
FROM TBCSPSAE301D WHERE LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND CRS_CD = #strEDU_CRS_CD# AND CRS_CDN = #CRS_CDN# AND UNPAID_AMT > 0) F, (SELECT DISTINCT AS RMRK_CN
FROM TBCSPSAE113B) G, (SELECT CEIL((TO_DATE(EDU_END_YMD, 'YYYYMMDD') - NEXT_DAY(TO_DATE(EDU_BGNG_YMD, 'YYYYMMDD') -1, '6') +1)/7) AS CEIL
FROM TBCSPSAE201D WHERE LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND CRS_CD = #strEDU_CRS_CD# AND CRS_CDN = #CRS_CDN# ) H /* 20240613 교육과정 점심식비에 금요일 미포함 계산식 조정으로 추가 */ WHERE B.LINK_YR = A.LINK_YR AND B.CRS_CD = A.CRS_CD AND C.LINK_YR = A.LINK_YR AND C.CRS_CD = A.CRS_CD AND C.CRS_CDN = B.CRS_CDN AND A.LINK_YR = SUBSTR(#strFromYm#, 0, 4) AND A.CRS_CD = #strEDU_CRS_CD# AND B.CRS_CDN = #CRS_CDN# GROUP BY B.EDU_BGNG_YMD, B.EDU_END_YMD, A.EDU_PRD_TXT