SELECT DISTINCT(SUBSTRING(B.DT, 7, 2) || '(' || SUBSTRING(UPPER(TO_CHAR(TO_DATE(B.DT), 'DAY', 'NLS_DATE_LANGUAGE=KOREAN')), 0, 1) || ')') AS SUBSTRING
FROM ( SELECT TO_CHAR(TO_DATE(#strYR_CD# || #strM# || '01', 'YYYYMMDD') + LEVEL-1, 'YYYYMMDD') AS TO_CHAR
FROM DUAL CONNECT BY LEVEL <= TO_DATE((SELECT TO_CHAR(LAST_DAY(#strYR_CD# || #strM# || '01'), 'YYYYMMDD')
FROM DUAL), 'YYYYMMDD') - (TO_DATE(#strYR_CD# || #strM# || '01', 'YYYYMMDD')-1) ) B ORDER BY DATE_NAME