SELECT DISTINCT( A.PFSR_ID),
    A.INFO_RLPR_NM /* 정보관계자명 */,
    (SELECT B.CD_NM
FROM TBDCMACM013C B WHERE B.CMM_CD_DVSN_CD = '1000312' AND B.CD = A.PFSR_DVSN_CD) AS PFSR_DVSN_NM , (SELECT B.CD_NM
FROM TBDCMACM013C B WHERE B.CMM_CD_DVSN_CD = '1000141' AND B.CD = A.PFSR_RK_CD) AS PFSR_RK_NM , (SELECT B.CD_NM
FROM TBDCMACM013C B WHERE B.CMM_CD_DVSN_CD = '1000136' AND B.CD = A.PFSR_SPH_CD) AS PFSR_SPH_NM , A.PFSR_RANK_NM , A.PFSR_BLT_NM , (SELECT MAX(B.LCT_DT)
FROM TBCSPSAE202D B WHERE B.PFSR_ID = A.PFSR_ID) AS LCT_DT , (SELECT B.CD_NM
FROM TBDCMACM013C B WHERE B.CMM_CD_DVSN_CD = '1000313' AND B.CD = A.VLD_DVSN_CD) AS VLD_DVSN_NM FROM TBCSPSAE109M A WHERE 1=1 AND REGEXP_LIKE (A.INFO_RLPR_NM, #strNAM#) AND A.PFSR_DVSN_CD = #strPFSR_DVSN_CD# AND A.PFSR_RK_CD = #strPFSR_RK_CD# AND A.VLD_DVSN_CD = #strVLD_DVSN_CD# AND FLOOR(MOD(MONTHS_BETWEEN(TO_CHAR(SYSDATE, 'yyyyMMdd'), (SELECT MAX(B.LCT_DT)
FROM TBCSPSAE202D B WHERE B.PFSR_ID = A.PFSR_ID)),0)*30.5) &lt;= #strRange# ORDER BY A.INFO_RLPR_NM