SELECT COUNT(A.BSC_SRNO)|| '/' || (SELECT AS BSC_SRNO
FROM TBCSPSAE132B) AS resvCnt FROM (SELECT DISTINCT A.BSC_SRNO
FROM TBCSPSAE501M A , TBCSPSAE501D B WHERE B.TRGT_NOP_ENO = A.TRGT_NOP_ENO AND B.APL_DT = A.APL_DT AND B.BSC_SRNO = A.BSC_SRNO AND B.COFM_STA_CD IN ('10', '30', '50', '60') AND B.APL_DT BETWEEN TO_CHAR(SYSDATE, 'YYYYMM') || '01' AND TO_CHAR(LAST_DAY(SYSDATE), 'YYYYMMDD') AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO#) A