SELECT SUBSTR(T2.DT, 7,2) || '(' || SUBSTRING(UPPER(TO_CHAR(TO_DATE(T2.DT), 'DAY', 'NLS_DATE_LANGUAGE=KOREAN')), 0, 1) || ')' AS SUBSTR
FROM ( SELECT TO_CHAR(TO_DATE(T1.BGNG_YMD, 'YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') AS TO_CHAR
FROM ( SELECT #strFromY# || #strFromM# || '01' AS BGNG_YMD /* 시작일자 */,
    TO_CHAR(LAST_DAY(#strToY# || #strToM# || '01'), 'YYYYMMDD') AS END_YMD /* 종료일자 */
FROM DUAL ) T1 CONNECT BY LEVEL <= (TO_DATE(T1.END_YMD, 'YYYYMMDD')+1) - TO_DATE(T1.BGNG_YMD, 'YYYYMMDD') ) T2 , TBCSPSAE133B T3 , TBCSPSAE134B T4 WHERE T2.DT = T3.APL_DT AND T2.DT = TO_CHAR(TO_DATE(T4.END_DDL_DT)-1,'YYYYMMDD')