SELECT B.TRGT_NOP_ENO /* 대상인원직원전산번호 */,
    B.USER_ID /* 사용자아이디 */,
    B.USER_NM /* 사용자명 */,
    B.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    C.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    REPLACE(B.CNSTN_MBCMT_HOME_TELNO, '-', '') AS REPLACE,
    '' AS UE_NOP_CNT,
    '' AS IO_CAR_TXT,
    '' AS UE_PPS_TXT,
    DECODE(D.CNT, '', 0, D.CNT) || '/' || (SELECT AS CNT
FROM TBCSPSAE132B) AS MN_BOKG_NBC , (SELECT BOKG_POSS_RM_CNT
FROM TBCSPSAE132B) - A.POSS_CNT AS BOKG_POSS_RM_CNT , (SELECT COK_PMS_CNT
FROM TBCSPSAE134B WHERE END_DDL_DT IN (SELECT TO_CHAR(TO_DATE(T1.BGNG_YMD, 'YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') AS TO_CHAR
FROM ( SELECT TO_CHAR(WEEK_START, 'YYYYMMDD') AS BGNG_YMD /* 시작일자 */,
    TO_CHAR(WEEK_END, 'YYYYMMDD') AS END_YMD /* 종료일자 */
FROM ( SELECT TRUNC (START_DT + LEVEL, 'D') + 1 AS TRUNC,
    TRUNC (START_DT + LEVEL, 'D') + 7 AS TRUNC
FROM ( SELECT TO_DATE(#strDATE#) - 1 AS TO_DATE,
    TO_DATE(#strDATE#) AS END_YMD /* 종료일자 */
FROM DUAL ) CONNECT BY LEVEL <= END_YMD - START_DT ) ) T1 CONNECT BY LEVEL <= (TO_DATE(T1.END_YMD, 'YYYYMMDD')+1) - TO_DATE(T1.BGNG_YMD, 'YYYYMMDD')) ) - E.COK_USE_CNT AS COK_PMS_CNT , DECODE(NVL(#strLENGTH#, 0), 0, (SUBSTR(#strDATE#, 0, 4) || '-' || SUBSTR(#strDATE#, 5, 2) || '-' || SUBSTR(#strDATE#, 7, 2) || '(' || SUBSTRING(UPPER(TO_CHAR(TO_DATE(#strDATE#), 'DAY', 'NLS_DATE_LANGUAGE=KOREAN')), 0, 1) || ')'), (SUBSTR(#strDATE#, 0, 4) || '-' || SUBSTR(#strDATE#, 5, 2) || '-' || SUBSTR(#strDATE#, 7, 2) || '(' || SUBSTRING(UPPER(TO_CHAR(TO_DATE(#strDATE#), 'DAY', 'NLS_DATE_LANGUAGE=KOREAN')), 0, 1) || ')' || ', ' || SUBSTR(TO_CHAR((TO_DATE(#strDATE#)+#strLENGTH#), 'YYYYMMDD'), 0, 4) || '-' || SUBSTR(TO_CHAR((TO_DATE(#strDATE#)+#strLENGTH#), 'YYYYMMDD'), 5, 2) || '-' || SUBSTR(TO_CHAR((TO_DATE(#strDATE#)+#strLENGTH#), 'YYYYMMDD'), 7, 2) || '(' || SUBSTRING(UPPER(TO_CHAR(TO_DATE(TO_CHAR((TO_DATE(#strDATE#)+#strLENGTH#), 'YYYYMMDD')), 'DAY', 'NLS_DATE_LANGUAGE=KOREAN')), 0, 1) || ')')) AS USE_DT , TO_CHAR(SYSDATE, 'YYYYMMDD') AS APL_DT , (SUBSTR(#strDATE#, 0, 4) || '년 ' || SUBSTR(#strDATE#, 5, 2) || '월 ' || SUBSTR(#strDATE#, 7, 2) || '일') AS USE_FROM_DT , (SUBSTR( TO_CHAR(TO_DATE(#strDATE#, 'YYYYMMDD')+NVL(#strLENGTH#, 0), 'YYYYMMDD'), 0, 4) || '년 ' || SUBSTR(TO_CHAR(TO_DATE(#strDATE#, 'YYYYMMDD')+NVL(#strLENGTH#, 0), 'YYYYMMDD'), 5, 2) || '월 ' || SUBSTR(TO_CHAR(TO_DATE(#strDATE#, 'YYYYMMDD')+NVL(#strLENGTH#, 0) + 1, 'YYYYMMDD'), 7, 2) || '일') AS USE_TO_DT , '' AS USE_FEE , '' AS BOKG_DT FROM (SELECT COUNT(B.TRGT_NOP_ENO) AS TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBCSPSAE501D B WHERE B.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND B.COFM_STA_CD IN ('10', '30', '50', '60') AND B.USE_STR_DT BETWEEN TO_CHAR(TRUNC(TO_DATE(#strDATE#),'IW'),'YYYYMMDD') AND TO_CHAR(TRUNC(TO_DATE(#strDATE#),'IW')+6,'YYYYMMDD')) A , TBDCMACM001M B , TBDCMACM002M C , ( SELECT COUNT(NVL(A.BSC_SRNO, 0)) AS BSC_SRNO
FROM (SELECT DISTINCT A.BSC_SRNO
FROM TBCSPSAE501M A , TBCSPSAE501D B WHERE B.TRGT_NOP_ENO = A.TRGT_NOP_ENO AND B.APL_DT = A.APL_DT AND B.BSC_SRNO = A.BSC_SRNO AND B.COFM_STA_CD IN ('10', '30', '50', '60') AND B.APL_DT BETWEEN TO_CHAR(SYSDATE, 'YYYYMM') || '01' AND TO_CHAR(LAST_DAY(SYSDATE), 'YYYYMMDD') AND A.TRGT_NOP_ENO = #TRGT_NOP_ENO# ) A ) D , (SELECT COUNT(A.COK_USE_CNT) AS COK_USE_CNT
FROM (SELECT A.DT,
    A.TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM (SELECT TO_DATE(B.USE_STR_DT, 'yyyymmdd') AS TO_DATE,
    B.TRGT_NOP_ENO /* 대상인원직원전산번호 */
FROM TBCSPSAE501M A , TBCSPSAE501D B WHERE B.TRGT_NOP_ENO = A.TRGT_NOP_ENO AND B.APL_DT = A.APL_DT AND B.BSC_SRNO = A.BSC_SRNO AND A.USE_YN = 'Y' AND B.USE_STR_DT IN ( SELECT TO_CHAR(TO_DATE(T1.BGNG_YMD, 'YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') AS TO_CHAR
FROM ( SELECT TO_CHAR(WEEK_START, 'YYYYMMDD') AS BGNG_YMD /* 시작일자 */,
    TO_CHAR(WEEK_END, 'YYYYMMDD') AS END_YMD /* 종료일자 */
FROM ( SELECT TRUNC (START_DT + LEVEL, 'D') + 1 AS TRUNC,
    TRUNC (START_DT + LEVEL, 'D') + 7 AS TRUNC
FROM ( SELECT TO_DATE(#strDATE#) - 1 AS TO_DATE,
    TO_DATE(#strDATE#) AS END_YMD /* 종료일자 */
FROM DUAL ) CONNECT BY LEVEL <= END_YMD - START_DT ) ) T1 CONNECT BY LEVEL <= (TO_DATE(T1.END_YMD, 'YYYYMMDD') + 1) - TO_DATE(T1.BGNG_YMD, 'YYYYMMDD') ) ) A GROUP BY A.DT, A.TRGT_NOP_ENO ) A ) E WHERE B.TRGT_NOP_ENO = #TRGT_NOP_ENO# AND C.SRECS_DEPT_CD = B.SRECS_DEPT_CD