SELECT A.SUN,
    (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.SUN) AS SUN_FLAG , A.MON , (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.MON) AS MON_FLAG , A.TUES , (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.TUES) AS TUES_FLAG , A.WED , (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.WED) AS WED_FLAG , A.THUR , (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.THUR) AS THUR_FLAG , A.FRI , (SELECT DECODE(COUNT(*), 1, 'T', 'F')
FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.FRI) AS FRI_FLAG , A.SAT , (SELECT DECODE(COUNT(*), 1, 'T', DECODE((SELECT COUNT(*)
FROM TB_BADDED013B WHERE INFO_YR = #INFO_YR# AND HLDY_CRTR_MND = #HLDY_CRTR_MND# || A.SAT), 1, 'T', 'F')) FROM TBCSPSAE133B A WHERE SUBSTR(A.APL_DT, 0, 6) = #INFO_YR# || #HLDY_CRTR_MND# AND SUBSTR(A.APL_DT, 7, 2) = A.SAT) AS SAT_FLAG , #INFO_YR# AS LINK_YR , #HLDY_CRTR_MND# AS MONTH FROM ( SELECT LPAD(MIN(DECODE(D1.D, '1', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '2', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '3', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '4', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '5', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '6', D1.DD)), 2, '0') AS LPAD,
    LPAD(MIN(DECODE(D1.D, '7', D1.DD)), 2, '0') AS LPAD
FROM ( SELECT LEVEL,
    TO_CHAR(MAKE_DATES+ LEVEL -1, 'D') AS TO_CHAR
FROM (SELECT TO_DATE( #INFO_YR# || #HLDY_CRTR_MND# || '01', 'YYYYMMDD') AS TO_DATE
FROM DUAL) CONNECT BY (MAKE_DATES+LEVEL-1) <= LAST_DAY(MAKE_DATES) ) D1 GROUP BY DD-D ORDER BY SAT ) A