SELECT GRNDS_EXMN_AEX_SQNO /* 현장조사수용비순번 */,
    RSCH_ASMT_TTL /* 연구과제제목 */,
    USE_STR_DT,
    USE_END_DT
FROM TBCSPSAE136B WHERE 1=1 AND RSCH_ASMT_TTL LIKE '%' || #strTIT_NM# || '%' AND ( ( SUBSTR(USE_STR_DT, 0, 6) BETWEEN #strFromY# || #strFromM# AND #strToY# || #strToM# OR SUBSTR(USE_END_DT, 0, 6) BETWEEN #strFromY# AND #strToY# ) OR (#strFromY# || #strFromM# BETWEEN SUBSTR(USE_STR_DT, 0, 6) AND SUBSTR(USE_END_DT, 0, 6) OR #strToY# || #strToM# BETWEEN SUBSTR(USE_STR_DT, 0, 6) AND SUBSTR(USE_END_DT, 0, 6) ) ) ORDER BY GRNDS_EXMN_AEX_SQNO