MERGE INTO TBCSPSAE802M T1 USING ( SELECT #YE# AS INFO_YR /* 정보년도 */,
    #DGE# AS TPOF_CYCL /* 제보차수 */,
    #ATT_FL_DVSN_CD# AS #ATT_FL_DVSN_CD#
FROM DUAL ) T2 ON ( T1.INFO_YR = T2.INFO_YR AND T1.TPOF_CYCL = T2.TPOF_CYCL AND T1.ATT_FL_DVSN_CD = T2.ATT_FL_DVSN_CD ) WHEN MATCHED THEN UPDATE SET CVLCPT_DOC_ID = #CVLCPT_DOC_ID# /* 민원문서아이디 */,  LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( INFO_YR /*연도*/ ,TPOF_CYCL /*차수*/ ,ATT_FL_DVSN_CD /*첨부파일구분코드*/ ,CVLCPT_DOC_ID /*첨부파일문서ID*/ ,FRST_REG_DT /*최초등록일시*/ ,LAST_MDFCN_DT /*최종수정일시*/ ) VALUES ( #YE# /*연도*/ ,#DGE# /*차수*/ ,#ATT_FL_DVSN_CD# /*첨부파일구분코드*/ ,#CVLCPT_DOC_ID# /*첨부파일문서ID*/ ,SYSDATE /*최초등록일시*/ ,SYSDATE /*최종수정일시*/ )