SELECT /*CSPURA001EMainListExcel*/ F_CODE_NM('1000294',A.RSCH_ASMT_SECD) AS F_CODE_NM,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    F_USR_INFO(A.#N/A,'2') ||DECODE(A.#N/A,NULL,NULL,',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) AS F_USR_INFO,
    SUBSTR(A.RSCH_ASMT_RPT_YMD, 0, 4) || '-' || SUBSTR(A.RSCH_ASMT_RPT_YMD, 5, 2) || '-' || SUBSTR(A.RSCH_ASMT_RPT_YMD, 7, 2) AS SRECS_RPT_YMD /* 심사재심의보고일자 */,
    F_CODE_NM('1000605',A.RSCH_ASMT_KDCD) AS F_CODE_NM,
    F_CODE_NM('1000654',A.RSCH_ASMT_RSCH_TPCD) AS F_CODE_NM
FROM TB_CSPURA001M A WHERE 1=1 AND A.SRECS_RPT_YMD &gt;= #fromYmd# AND A.SRECS_RPT_YMD &lt;= #toYmd# AND A.RSCH_ASMT_SECD IN ( SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #STD_TASK_DVSN_CD# ||',' AS #STD_TASK_DVSN_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND (A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A#) AND RSCH_ASMT_TTL LIKE '%'||#STD_TASK_NM#||'%' AND RSCH_ASMT_RSCH_TPCD = #RSCH_ASMT_RSCH_TPCD# AND ETC_LBSVC_KDCD = #ETC_LBSVC_KDCD# ORDER BY A.SRECS_RPT_YMD DESC