SELECT /*CSPURA001EMainSelect*/ A.RSCH_YR AS RSCH_YR /* 연구년도 */,
    A.RSCH_ASMT_SQNO /* 연구과제순번 */,
    A.RSCH_ASMT_SECD /* 연구과제구분코드 */,
    F_CODE_NM('1000294',A.RSCH_ASMT_SECD) AS F_CODE_NM,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    CASE WHEN LENGTHB(RSCH_ASMT_TTL) > 50 THEN SUBSTRB(A.RSCH_ASMT_TTL,1,50)||'...' ELSE A.RSCH_ASMT_TTL END AS LENGTHB,
    A.#N/A /* 연구과제담당자직원전산번호1 */,
    (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS CHGR_NAM1, A.#N/A as CHGR_CNB2, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS CHGR_NAM2, A.#N/A as CHGR_CNB3, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS CHGR_NAM3, A.#N/A as CHGR_CNB4, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS CHGR_NAM4, F_USR_INFO(A.#N/A,'2') ||DECODE(A.#N/A,NULL,NULL,',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) AS TOT_NM, A.SRECS_RPT_YMD, A.CVLCPT_DOC_ID, A.ADT_PLAN_DOC_TPCD, A.RSCH_ASMT_RMRK_CN, A.FRST_USER_ID, A.ETC_LBSVC_KDCD, F_CODE_NM('1000605',A.ETC_LBSVC_KDCD) AS AUD_STD_SPH_NM, A.RSCH_ASMT_RSCH_TPCD, F_CODE_NM('1000654',A.RSCH_ASMT_RSCH_TPCD) AS STD_TYP_NM, CASE WHEN (TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'))- TO_DATE(TO_CHAR(FRST_REG_DT,'YYYYMMDD'))) AND A.SRECS_RPT_YMD &gt;= #fromYmd# AND A.SRECS_RPT_YMD &lt;= #toYmd# AND A.RSCH_ASMT_SECD IN ( SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #STD_TASK_DVSN_CD# ||',' AS #STD_TASK_DVSN_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND (A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A#) AND RSCH_ASMT_TTL LIKE '%'||#STD_TASK_NM#||'%' AND RSCH_ASMT_RSCH_TPCD = #RSCH_ASMT_RSCH_TPCD# AND ETC_LBSVC_KDCD = #ETC_LBSVC_KDCD# AND (A.RSCH_ASMT_INST_CD = #RSCH_ASMT_INST_CD# OR A.RSCH_ASMT_INST_CD = #RSCH_ASMT_INST_CD# OR A.RSCH_ASMT_INST_CD = #RSCH_ASMT_INST_CD# OR A.RSCH_ASMT_INST_CD = #RSCH_ASMT_INST_CD#) AND RSCH_ASMT_RSCH_TPCD = '01' AND RSCH_ASMT_RSCH_TPCD = '13' AND RSCH_ASMT_RSCH_TPCD = '10' AND RSCH_ASMT_RSCH_TPCD = '14' AND RSCH_ASMT_RSCH_TPCD IN ('07','15') AND RSCH_ASMT_RSCH_TPCD IN ('12','16') AND RSCH_ASMT_RSCH_TPCD = '05' AND RSCH_ASMT_RSCH_TPCD IN ('02','03','04','11') AND RSCH_ASMT_RSCH_TPCD = '06' AND RSCH_ASMT_RSCH_TPCD = '08' AND RSCH_ASMT_RSCH_TPCD = '09' ORDER BY A.SRECS_RPT_YMD DESC