SELECT RSCH_YR /* 연구년도 */,
    RSCH_ASMT_SQNO /* 연구과제순번 */,
    RSCH_ASMT_RSCH_ACTV_SECD /* 연구과제연구활동구분코드 */,
    RSCH_ASMT_TTL /* 연구과제제목 */,
    #N/A /* 연구과제작성자직원전산번호1 */,
    #N/A /* 연구과제작성자직원전산번호2 */,
    #N/A /* 연구과제작성자직원전산번호3 */,
    #N/A /* 연구과제작성자직원전산번호4 */,
    (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS WRIT_PSN_NAM1, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS WRIT_PSN_NAM2, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS WRIT_PSN_NAM3, (SELECT USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS WRIT_PSN_NAM4, F_USR_INFO(A.#N/A,'2') ||DECODE(A.#N/A,NULL,NULL,',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) AS TOT_NM, CVLCPT_DOC_ID, ADT_PLAN_DOC_TPCD, BGNG_YMD, END_YMD, TO_CHAR(TO_DATE(BGNG_YMD, 'YYYY-MM-DD'),'YYYY-MM-DD') || ' ~ ' || TO_CHAR(TO_DATE(END_YMD, 'YYYY-MM-DD'), 'YYYY-MM-DD') AS STR_END_DT, RSCH_ASMT_RMRK_CN, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT, ETC_LBSVC_KDCD FROM TB_CSPURA002M A WHERE 1=1 AND RSCH_ASMT_RSCH_ACTV_SECD = '2' AND A.RSCH_ASMT_BGNG_YMD &gt;= #fromYmd# AND A.RSCH_ASMT_BGNG_YMD &lt;= #toYmd# AND A.RSCH_ASMT_END_YMD &gt;= #fromYmd# AND A.RSCH_ASMT_END_YMD &lt;= #toYmd# AND (A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A# OR A.#N/A = ##N/A#) AND RSCH_ASMT_TTL LIKE '%'||#TIT_NM#||'%' ORDER BY RSCH_YR DESC, TO_NUMBER(RSCH_ASMT_SQNO) DESC