SELECT TO_CHAR(TO_DATE(SRECS_RPT_YMD),'YYYY') AS TO_CHAR,
    RSCH_ASMT_TTL /* 연구과제제목 */,
    F_USR_INFO(A.#N/A,'2') ||DECODE(A.#N/A,NULL,NULL,',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) ||DECODE(A.#N/A,'','',',')||DECODE(A.#N/A,NULL,NULL,F_USR_INFO(A.#N/A,'2')) AS F_USR_INFO,
    TO_CHAR(TO_DATE(SRECS_RPT_YMD),'YYYY.MM') AS REG_DMND_YMD /* 등록요청일자 */,
    RSCH_ASMT_RMRK_CN /* 연구과제비고내용 */
FROM TB_CSPURA001M A WHERE RSCH_ASMT_RSCH_TPCD = #RSCH_ASMT_RSCH_TPCD# AND A.RSCH_ASMT_SECD = '3' AND TO_CHAR(TO_DATE(SRECS_RPT_YMD),'YYYY') &lt;= TO_CHAR(SYSDATE,'YYYY') -5 AND TO_CHAR(TO_DATE(SRECS_RPT_YMD),'YYYY') = #yy# ORDER BY SRECS_RPT_YMD DESC