SELECT /*CSPURA001EMainListExcel*/ A.RSCH_ASMT_TTL AS RSCH_ASMT_TTL /* 연구과제제목 */,
    F_USR_INFO(A.#N/A,'2') AS F_USR_INFO,
    SUBSTR(A.SRECS_RPT_YMD, 0, 4) || '-' || SUBSTR(A.SRECS_RPT_YMD, 5, 2) || '-' || SUBSTR(A.SRECS_RPT_YMD, 7, 2) AS SRECS_RPT_YMD /* 심사재심의보고일자 */,
    F_CODE_NM('1000605',A.ETC_LBSVC_KDCD) AS F_CODE_NM,
    A.RSCH_ASMT_PRFMNC_YR /* 연구과제실적년도 */,
    F_DPT_INFO(A.ETC_LBSVC_MNG_DEPT_CD, '1') AS PRCS_DEPT_NM /* 처리부서명 */,
    F_CODE_NM('1000835',ETC_LBSVC_RLS_SECD) AS RLS_SE_NM /* 공개구분명 */
FROM TB_CSPURA004M A WHERE 1=1 AND A.ETC_LBSVC_MNG_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, 'ALL', '', #strDptCd#))) AND A.SRECS_RPT_YMD &gt;= #fromYmd# AND A.SRECS_RPT_YMD &lt;= #toYmd# AND A.#N/A = ##N/A# AND RSCH_ASMT_TTL LIKE '%'||#STD_TASK_NM#||'%' AND ETC_LBSVC_KDCD = #ETC_LBSVC_KDCD# AND A.ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# ORDER BY A.SRECS_RPT_YMD DESC