SELECT /*CSPURA016EMainSelect*/ A.RSCH_YR AS RSCH_YR /* 연구년도 */,
    A.RSCH_ASMT_SQNO /* 연구과제순번 */,
    A.RSCH_ASMT_TTL /* 연구과제제목 */,
    CASE WHEN LENGTHB(RSCH_ASMT_TTL) > 50 THEN SUBSTRB(A.RSCH_ASMT_TTL,1,50)||'...' ELSE A.RSCH_ASMT_TTL END AS LENGTHB,
    A.#N/A /* 연구과제담당자직원전산번호1 */,
    (SELECT AS USER_NM /* 사용자명 */
FROM TBDCMACM001M WHERE TRGT_NOP_ENO = A.#N/A) AS CHGR_NAM1, F_USR_INFO(A.#N/A,'2') AS TOT_NM, A.SRECS_RPT_YMD, A.CVLCPT_DOC_ID, A.ADT_PLAN_DOC_TPCD, A.FRST_USER_ID, A.ETC_LBSVC_KDCD, F_CODE_NM('1000605',A.ETC_LBSVC_KDCD) AS AUD_STD_SPH_NM, CASE WHEN (TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'))- TO_DATE(TO_CHAR(FRST_REG_DT,'YYYYMMDD'))) AND A.SRECS_RPT_YMD &gt;= #fromYmd# AND A.SRECS_RPT_YMD &lt;= #toYmd# AND A.#N/A = ##N/A# AND RSCH_ASMT_TTL LIKE '%'||#STD_TASK_NM#||'%' AND ETC_LBSVC_KDCD = #ETC_LBSVC_KDCD# AND A.ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# ORDER BY A.SRECS_RPT_YMD DESC