SELECT USER_NM||NVL(DECODE(LENGTH(F_CODE_NM('1000173', A.JBGD_SECD)),2,'('||SUBSTR(F_CODE_NM('1000173', A.JBGD_SECD),1,1)||') ', ''),'')||D.ABR_DPT_NM_1 AS USER_NM /* 사용자명 */
FROM TBDCMACM002M D, TBDCMACM001M A WHERE D.USE_YN = 'Y' AND D.SRECS_DEPT_CD = A.SRECS_DEPT_CD AND A.SRECS_DEPT_CD != '880880' AND A.HDOF_SECD IN ('AAA','AAB','ABA','ABB') AND A.SRECS_DEPT_CD NOT IN ('150150','150160','150170') AND A.RQS_STA_CD = '3' ORDER BY A.USER_NM