SELECT USER_NM||DECODE(LENGTH(JBGD_NM),2,'('||SUBSTR(JBGD_NM,1,1)||')', '')||' '||CRU_TELNO||' '||ABR_DPT_NM_1 AS USER_NM /* 사용자명 */
FROM ( SELECT USER_NM /* 사용자명 */,
    F_CODE_NM('1000173', A.JBGD_SECD) AS JBGD_NM /* 직급명 */,
    D.ABR_DPT_NM_1,
    CASE WHEN LENGTH(A.CRU_TELNO) > 4 THEN SUBSTR(A.CRU_TELNO,LENGTH(A.CRU_TELNO)-3,LENGTH(A.CRU_TELNO)) ELSE A.CRU_TELNO END AS CRU_TELNO /* 부패전화번호 */
FROM TBDCMACM002M D, TBDCMACM001M A WHERE D.USE_YN = 'Y' AND D.SRECS_DEPT_CD = A.SRECS_DEPT_CD AND A.SRECS_DEPT_CD != '880880' AND A.HDOF_SECD IN ('AAA','AAB','ABA','ABB') AND A.SRECS_DEPT_CD NOT IN ('150150','150160','150170') AND A.RQS_STA_CD = '3' ) ORDER BY USER_NM