DECLARE BEGIN UPDATE TBDCMACM013C SET LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */,  CMM_CD_DVSN_CD = #CMM_CD_DVSN_CD# ,  CD = #CD# ,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# TASK_SECD = #TASK_SECD# FUNC_CAT_CD = #FUNC_CAT_CD# CD_NM = #CD_NM# USE_YN = NVL(#USE_YN#,'N') PRD_USE_YN = NVL(#PRD_USE_YN#,'N') BGNG_YMD = #BGNG_YMD# END_YMD = #END_YMD# OTPT_SEQ = #OTPT_SEQ# USR_DFN_TXT_1 = #USR_DFN_TXT_1# USR_DFN_TXT_2 = #USR_DFN_TXT_2# USR_DFN_TXT_3 = #USR_DFN_TXT_3# USR_DFN_TXT_4 = #USR_DFN_TXT_4# USR_DFN_TXT_5 = #USR_DFN_TXT_5# BK_CD_DVSN_NM = #BK_CD_DVSN_NM# BK_CD_NM = #BK_CD_NM# USR_DFN_TXT_6 = #USR_DFN_TXT_6# USR_DFN_TXT_7 = #USR_DFN_TXT_7# USR_DFN_TXT_8 = #USR_DFN_TXT_8# USR_DFN_TXT_9 = #USR_DFN_TXT_9# USR_DFN_TXT_10 = #USR_DFN_TXT_10# /* 최종메뉴아이디 */WHERE 1 = 1 AND CMM_CD_DVSN_CD = #CMM_CD_DVSN_CD# AND CD = #CD#; INSERT INTO TBDCMACM032H (LINK_NO, CON_USE_YN, CMM_CD_DVSN_CD, CD, TASK_SECD, FUNC_CAT_CD, CD_NM, USE_YN /* 사용여부 */, PRD_USE_YN, BGNG_YMD, END_YMD, OTPT_SEQ, USR_DFN_TXT_1, USR_DFN_TXT_2, USR_DFN_TXT_3, USR_DFN_TXT_4, USR_DFN_TXT_5, BK_CD_DVSN_NM, BK_CD_NM, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) SELECT ( (SELECT NVL(MAX(LINK_NO),0)+1 AS LINK_NO /* 연계번호 */
FROM TBDCMACM032H)) AS LINK_NO /* 연계번호 */ , 'N' AS CON_USE_YN , CMM_CD_DVSN_CD , CD , TASK_SECD , FUNC_CAT_CD , CD_NM , USE_YN , PRD_USE_YN , BGNG_YMD , END_YMD , OTPT_SEQ , USR_DFN_TXT_1 , USR_DFN_TXT_2 , USR_DFN_TXT_3 , USR_DFN_TXT_4 , USR_DFN_TXT_5 , BK_CD_DVSN_NM , BK_CD_NM , #LAST_USER_ID# AS LAST_USER_ID , #LAST_USER_ID# AS LAST_USER_ID , #LAST_PRCS_DEPT_CD# AS LAST_PRCS_DEPT_CD , #LAST_MENU_ID# AS LAST_MENU_ID , SYSDATE AS FRST_REG_DT , SYSDATE AS LAST_MDFCN_DT FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = #CMM_CD_DVSN_CD# AND CD = #CD# ; END;