DECLARE BEGIN UPDATE TBDCMACM015M SET INST_CD = INST_CD /* 기관코드 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, SLCT_JRSD_SG_SECD = SUBSTR(#COPT_SG_2_CD#,0,3) /* 청탁소관부호구분코드 */, COPT_SG_2_CD = #COPT_SG_2_CD# , ADMN_ORG_DVSN_CD = #ADMN_ORG_DVSN_CD# PROF_RBPRSN_INST_NM = #PROF_RBPRSN_INST_NM# RGN_CD = #RGN_CD# INST_SECD = #INST_SECD# ORG_DGE_CD = #ORG_DGE_CD# CRU_TELNO = #CRU_TELNO# CRU_ZIP = #CRU_ZIP# OTSD_EXPRT_ADDR =#OTSD_EXPRT_ADDR# CRU_CVLPR_DADDR = #CRU_CVLPR_DADDR# CNSTN_MBCMT_RDNM_ADDR = #CNSTN_MBCMT_RDNM_ADDR# OTSD_EXPRT_RDNM_DADDR = #OTSD_EXPRT_RDNM_DADDR# USE_DVSN_NO = #USE_DVSN_NO# ENFM_DPT_CD = #ENFM_DPT_CD# PASA_USE_YN_CD = #PASA_USE_YN_CD# SLCT_PBNST_SECD = #SLCT_PBNST_SECD# WHERE 1 = 1 AND INST_CD = #INST_CD#; INSERT INTO TBDCMACM034H(LINK_NO, CON_USE_YN, INST_CD, PROF_RBPRSN_INST_NM, INST_SECD, ORG_DGE_CD, SLCT_JRSD_SG_SECD, CRU_TELNO, CRU_ZIP, OTSD_EXPRT_ADDR, CRU_CVLPR_DADDR, CNSTN_MBCMT_RDNM_ADDR, OTSD_EXPRT_RDNM_DADDR, USE_DVSN_NO, PROF_RBPRSN_CRT_YMD, RMRK_CN, ENFM_DPT_CD, INST_CLSF_TPCD, CVPT_TON_ORG_YN, CHG_DT, PROF_RBPRSN_BRNO, PROF_RBPRSN_ABL_YMD, SLCT_PBNST_SECD, ENAIS_PST_INQ_NMTM, PASA_USE_YN_CD, COPT_SG_2_CD, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT, RGN_CD /* 추가 - 2015.05.26 yyh */, ADMN_ORG_DVSN_CD) SELECT (SELECT NVL(MAX(LINK_NO),'0')+1 AS LINK_NO /* 연계번호 */
