DECLARE BEGIN UPDATE TBDCMACM002M SET SRECS_DEPT_CD = #SRECS_DEPT_CD# /* 심사재심의부서코드 */,  CNSTN_MBCMT_DEPT_NM = #CNSTN_MBCMT_DEPT_NM# /* 자문위원부서명 */,  USE_YN = #USE_YN# /* 사용여부 */,  LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */,  LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */,  LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */,  LAST_MENU_ID = #LAST_MENU_ID# CRU_TELNO = #CRU_TELNO# ABR_DPT_NM_1 = #ABR_DPT_NM_1# ABR_DPT_NM_2 = #ABR_DPT_NM_2# FRML_DPT_NM = #FRML_DPT_NM# STND_DPT_CD = #STND_DPT_CD# CNSTN_MBCMT_FAX_NO = #CNSTN_MBCMT_FAX_NO# OW_DVDT_TXT = #OW_DVDT_TXT# HIRK_DPT_CD = #HIRK_DPT_CD# CRU_SORT_SQNO = #CRU_SORT_SQNO# ORZT_DVSN_CD = #ORZT_DVSN_CD# DEPT_TPCD = #DEPT_TPCD# IMD_CD = #IMD_CD# DPT_TP_CD_2 = #DPT_TP_CD_2# LOC_DVSN_CD = #LOC_DVSN_CD# /* 의원면직수신대상여부 추가 - 2016.07.21 yyh */ ASBM_RCPT_TG_YN = #ASBM_RCPT_TG_YN# /* 홈페이지 부서공개 여부 추가 - 2018.10.04 EUNOK */ HOMP_OPN = #HOMP_OPN# /* 홈페이지 연락처공개 여부 추가 - 2018.10.04 EUNOK */ HOMP_SEC = #HOMP_SEC# /* 홈페이지 직위약칭코드 추가 - 2018.10.04 EUNOK */ HOMP_DVDT_CD = #HOMP_DVDT_CD# /* 홈페이지 직위약칭명 추가 - 2018.10.04 EUNOK */ HOMP_DVDT_NM = #HOMP_DVDT_NM# /* 홈페이지사무분장내용 추가 - 2019.08.28 yyh */ HOMP_OW_DVDT_TXT = #HOMP_OW_DVDT_TXT# WHERE 1=1 AND SRECS_DEPT_CD = #SRECS_DEPT_CD# ; INSERT INTO TBDCMACM030H (LINK_NO /* 연계번호 */, CON_USE_YN, SRECS_DEPT_CD /* 부서코드 */, CNSTN_MBCMT_DEPT_NM /* 부서명 */, CRU_TELNO /* 전화번호 */, USE_YN /* 사용여부 */, ABR_DPT_NM_1 /* 약칭부서명1 */, ABR_DPT_NM_2 /* 약칭부서명2 */, FRML_DPT_NM /* 공식부서명 */, STND_DPT_CD /* 표준부서코드 */, CNSTN_MBCMT_FAX_NO /* 팩스번호 */, IMD_CD /* 직속코드 */, DEPT_TPCD /* 부서타입코드 */, DPT_SNO /* 부서순번 */, CRU_ZIP /* 우편번호 */, OTSD_EXPRT_ADDR /* 주소 */, CRU_CVLPR_DADDR /* 상세주소 */, WEB_SITE_ADDR /* 홈페이지주소 */, SAL_DPT_CD /* 급여부서코드 */, SAL_DPT_NM /* 급여부서명 */, BK_DPT_NM /* 이전부서명 */, OW_DVDT_TXT /* 사무분장내용 */, HIRK_DPT_CD /* 상위부서코드 */, ORZT_DVSN_CD /* 조직구분코드 */, CRU_SORT_SQNO /* 정렬순번 */, DPTRE_RGN_SECD /* 출발지역코드 */, ENFM_WHLS_CHGR_CNB /* 집행전말담당자전산번호 */, HOMP_OPN /* 홈페이지공개여부 */, HOMP_SEC /* 홈페이지연락처공개여부 */, HOMP_DVDT_NM /* 홈페이지용직위약칭 */, HOMP_OW_DVDT_TXT /* 홈페이지사무분장내용 추가 - 2019.08.28 yyh */, FRST_USER_ID, FRST_REG_DT, LAST_MENU_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MDFCN_DT) SELECT ( (SELECT NVL(MAX(LINK_NO),0)+1 AS LINK_NO /* 연계번호 */
FROM TBDCMACM030H)) AS LINK_NO /* 연계번호 */ , 'N' AS CON_USE_YN , SRECS_DEPT_CD , CNSTN_MBCMT_DEPT_NM , CRU_TELNO , USE_YN , ABR_DPT_NM_1 , ABR_DPT_NM_2 , FRML_DPT_NM , STND_DPT_CD , CNSTN_MBCMT_FAX_NO , IMD_CD , DEPT_TPCD , DPT_SNO , CRU_ZIP , OTSD_EXPRT_ADDR , '' CRU_CVLPR_DADDR , WEB_SITE_ADDR , '' SAL_DPT_CD , '' SAL_DPT_NM , '' BK_DPT_NM , OW_DVDT_TXT , HIRK_DPT_CD , ORZT_DVSN_CD , CRU_SORT_SQNO , '' DPTRE_RGN_SECD , ENFM_WHLS_CHGR_CNB , HOMP_OPN , HOMP_SEC , HOMP_DVDT_NM , HOMP_OW_DVDT_TXT , #LAST_USER_ID# AS FRST_USER_ID , SYSDATE AS FRST_REG_DT , #LAST_MENU_ID# AS LAST_MENU_ID , #LAST_USER_ID# AS LAST_USER_ID , #LAST_PRCS_DEPT_CD# AS LAST_PRCS_DEPT_CD , SYSDATE AS LAST_MDFCN_DT FROM TBDCMACM002M WHERE SRECS_DEPT_CD=#SRECS_DEPT_CD# ; END;