SELECT T2.RVW_DOC_ID /* 검토문서아이디 */
FROM TB_BADDED103M T1 INNER JOIN TB_BADDED118M T2 ON T1.ADT_YR = T2.ADT_YR AND T1.ADT_NO = T2.ADT_NO AND T1.DOC_ID = #TPOF_RFRNC_ID# /* 감사보고서 DOC_ID */ AND T1.DOC_ID = T2.DOC_ID AND T1.RPTP_KDCD = 'A10600200' UNION ALL SELECT T1.DOC_ID /* 문서아이디 */
FROM TB_BADDED103M T1 WHERE T1.RFRNC_ID = #TPOF_RFRNC_ID# /* 감사보고서 DOC_ID */ AND T1.RPTP_KDCD = 'A10600410'