UPDATE PTL_ORDER_RECEIVE_LIST SET COMP_CMMT = '',  COMP_DT = TO_CHAR(SYSDATE,'yyyymmddhh24miss') WHERE ADT_RPTP_EDT_HSTRY_SQNO IN( SELECT TO_CHAR(MAX(ADT_RPTP_EDT_HSTRY_SQNO) ) AS ADT_RPTP_EDT_HSTRY_SQNO /* 감사보고서편집이력순번 */
FROM PTL_ORDER_LIST WHERE DOC_NO = #ATRZ_DOC_NO# AND SRT_NO IN( SELECT APVR_SEQ
FROM TBDCMACM023L WHERE ATRZ_DOC_NO = #ATRZ_DOC_NO# AND APV_SNO = #APV_SNO# AND APRVR_ENO <> #APVR_CNB# AND APRVR_KDCD = '6' ) )