DELETE FROM PTL_ORDER_RECEIVE_LIST WHERE ADT_RPTP_EDT_HSTRY_SQNO = ( SELECT TO_CHAR(MAX(ADT_RPTP_EDT_HSTRY_SQNO)) AS ADT_RPTP_EDT_HSTRY_SQNO /* 감사보고서편집이력순번 */
FROM PTL_ORDER_LIST WHERE DOC_NO = #ATRZ_DOC_NO# ) AND SRT_NO IN ( SELECT APVR_SEQ
FROM TBDCMACM023L WHERE ATRZ_DOC_NO = #ATRZ_DOC_NO# AND TPOF_ATRZ_SECD = #TPOF_ATRZ_SECD# AND (SUBSTR(APVR_STA_CD,0,1) <> 2 OR APVR_STA_CD IS NULL) )