MERGE INTO PTL_ORDER_RECEIVE_LIST A USING ( SELECT #USR_ID# AS USER_ID /* 사용자아이디 */,
    #SEQ# AS ADT_RPTP_EDT_HSTRY_SQNO /* 감사보고서편집이력순번 */,
    #APVR_SEQ# AS #APVR_SEQ#
FROM DUAL ) B ON ( A.USER_ID = B.USER_ID AND A.ADT_RPTP_EDT_HSTRY_SQNO = B.ADT_RPTP_EDT_HSTRY_SQNO AND A.SRT_NO = B.SRT_NO ) WHEN MATCHED THEN UPDATE SET COMP_CMMT = #COMP_CMMT# ,  COMP_DT = TO_CHAR(SYSDATE,'yyyymmddhh24miss') WHEN NOT MATCHED THEN INSERT INSERT ( ADT_RPTP_EDT_HSTRY_SQNO , USER_ID , COMP_CMMT , PROF_RBPRSN_CRT_YMD , COMP_DT , SRT_NO ) VALUES ( #SEQ# , #USR_ID# , #COMP_CMMT# , TO_CHAR(SYSDATE,'yyyymmddhh24miss') , TO_CHAR(SYSDATE,'yyyymmddhh24miss') , #APVR_SEQ# )