SELECT T2.CNSTN_MBCMT_DEPT_NM||' '||T1.RANK_NM_2||'('||T1.JBGD_SORT_SEQ||') '||T1.USER_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    'Oasys ID : '||T1.USER_ID||' / 전산번호 : '||T1.TRGT_NOP_ENO||' / 구내번호 : '||T1.CRU_TELNO||' / 핸드폰 : '||T1.CNSTN_MBCMT_HOME_TELNO AS USER_ID /* 사용자아이디 */,
    T1.OTSD_EXPRT_ADDR AS OTSD_EXPRT_ADDR /* 외부전문가주소 */,
    '입사일 : '||TO_CHAR(TO_DATE(T1.CRNT_DPT_APM_DT),'YYYY.MM.DD') ||'('||(TO_CHAR(SYSDATE,'YYYY')-TO_CHAR(TO_DATE(T1.CRNT_DPT_APM_DT),'YYYY')) ||'년) / 현직급임용일 : '||TO_CHAR(TO_DATE(T1.NOW_JBGD_APNTMN_YMD),'YYYY.MM.DD') ||'('||(TO_CHAR(SYSDATE,'YYYY')-TO_CHAR(TO_DATE(T1.NOW_JBGD_APNTMN_YMD),'YYYY'))||'년)' AS TO_CHAR,
    ( SELECT '감사부서 : '||CEIL((COUNT(*)/12))||'년('||COUNT(*)||')'
FROM TB_CSPBII180L S1 WHERE S1.TRGT_NOP_ENO = T1.TRGT_NOP_ENO AND DEPT_TPCD = 'A' AND SRECS_DEPT_CD NOT IN ('7777','770770')) ||' / '||( SELECT '지원부서 : '||CEIL((COUNT(*)/12))||'년('||COUNT(*)||')'
FROM TB_CSPBII180L S1 WHERE S1.TRGT_NOP_ENO = T1.TRGT_NOP_ENO AND DEPT_TPCD = 'S' AND SRECS_DEPT_CD NOT IN ('7777','770770')) ||' / '||( SELECT '파견.휴직 : '||CEIL((COUNT(*)/12))||'년('||COUNT(*)||')'
FROM TB_CSPBII180L S1 WHERE S1.TRGT_NOP_ENO = T1.TRGT_NOP_ENO AND SRECS_DEPT_CD IN ('7777','770770')) AS INSA2 ,NULL AS INSA3 ,'실지감사 '||( SELECT COUNT(*)
FROM ( SELECT DISTINCT S3.ADT_YR AS ADT_YR /* 감사년도 */,
    S3.ADT_NO /* 감사번호 */
FROM TBBADDED011L S1 ,TBBADDED002M S2 ,TBBADDED001M S3 WHERE S1.BZTRVR_ENO = #TRGT_NOP_ENO# AND S1.BZTRP_YR = S2.BZTRP_YR AND S1.BZTRP_SQNO = S2.BZTRP_SQNO AND S2.REL_ADT_YR = S3.CVLCPT_ADT_YR AND S2.REL_ADT_NO = S3.ADT_NO AND SUBSTRING(S3.ADT_KDCD,3,1) = '5' ) )||'회' AS AUD1 ,'' AUD2 ,'' AUD3 ,'' AUD4 ,'' AS AOE ,'제출 : ' || ( SELECT COUNT(*)
FROM TBCSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO )||'건 ( 입건 : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD IN ('31','34') )||'건 / 감사활용 : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD = '35' )||'건 / 불문 : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD = '36' )||'건 / 낭비(감점) : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD = '37' )||'건 / 공람완결 : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD = '77' )||'건 / 종결 : ' ||( SELECT COUNT(*)
FROM TB_CSPBII100M S1 WHERE S1.PRSNR_ENO = T1.TRGT_NOP_ENO AND S1.PRCS_RSLT_SECD = '88' )||' )' AS BII1 ,'' BII2 ,'등록 : ' ||( SELECT COUNT(*)
FROM TBAEPGKM001M S1 WHERE S1.OTSD_EXPRT_RGTR_ENO = T1.TRGT_NOP_ENO) ||'건 (채택 : 0건)' AS GKM ,'제출 : ' ||( SELECT COUNT(*)
FROM TBCSPGSU001M S1 WHERE S1.PRPS_PEN_CNB = T1.TRGT_NOP_ENO AND S1.PRPS_STA_CD <> 'A') ||'건 (채택 : ' ||( SELECT COUNT(*)
FROM TBCSPGSU001M S1 WHERE S1.PRPS_PEN_CNB = T1.TRGT_NOP_ENO AND S1.PRPS_STA_CD = 'N') ||'건)' AS GSU ,( SELECT REPLACE(AGGR_CONCAT(F_CODE_NM('1000822',S1.RWD_SER_DTL_CD)||'('||TO_CHAR(TO_DATE(S1.RWD_DT),'YYYY.MM.DD')||')','//'),'//',CHR(10)) AS REPLACE
FROM TBDCMACM042M S1, TBDCMACM043D S2 WHERE S1.RWD_NO = S2.RWD_NO AND S2.RWD_TG_STF_CNB = T1.TRGT_NOP_ENO ) AS POSANG ,'처리 : '||( SELECT COUNT(*)
FROM TB_CSPDCP201M S1 WHERE PRCS_PIC_ID = T1.USER_ID )||'건' AS ETC1 ,'처리 : '||( SELECT COUNT(*)
FROM TB_CSPDCP813M S1 WHERE PRCS_PIC_ID = T1.USER_ID )||'건' AS ETC2 ,'처리 : '||( SELECT COUNT(*)
FROM TB_CSPEAR001M S1 WHERE PRCS_PIC_ID = T1.USER_ID OR ACADT_DCSN_PIC_ID = T1.USER_ID )||'건' AS ETC3 ,'처리 : '||( SELECT COUNT(*)
FROM TB_BADFRE012M S1 WHERE SRECS_PRCS_PIC_SECD = T1.TRGT_NOP_ENO )||'건' AS ETC4 ,'연구 : '||( SELECT COUNT(*)
FROM TB_CSPURA001M S1 WHERE #N/A = T1.TRGT_NOP_ENO OR #N/A = T1.TRGT_NOP_ENO OR #N/A = T1.TRGT_NOP_ENO OR #N/A = T1.TRGT_NOP_ENO )||'건' AS ETC5 ,'모니터링보고서 : '||( SELECT COUNT(*)
FROM TBBADCMR009M S1 WHERE RGTR_ID = T1.TRGT_NOP_ENO )||'건' AS ETC6 FROM TBDCMACM001M T1 ,TBDCMACM002M T2 WHERE T1.SRECS_DEPT_CD = T2.SRECS_DEPT_CD AND T1.TRGT_NOP_ENO = #TRGT_NOP_ENO#