SELECT APL_NAM,
    COUNT(PMT_AM) AS PMT_AM,
    NVL(SUM(PMT_AM),0) AS PMT_AM,
    NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBDCMACM065M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND (SPH ='01' OR SPH ='08') AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) GROUP BY APL_NAM ),0) AS PMT_SUM_01 /* 직무분야건 표시 */ ,NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBDCMACM065M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND (SPH ='02' OR SPH ='06' OR SPH ='07') AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) GROUP BY APL_NAM ),0) AS PMT_SUM_02 /* 어학분야건 표시 */ ,NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBDCMACM065M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH ='03' AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) /* 승인된것만 표시 */ GROUP BY APL_NAM ),0) AS PMT_SUM_03 /* 도서분야건 표시 */ FROM TBDCMACM065M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH IN ('01','02','03','06','07','08') AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) /* 신청~승인된것만 표시 */ GROUP BY APL_NAM