SELECT D1.FRS_MDM_SQNO /* 포렌식매체순번 */,
    D1.DOC_DES,
    CASE WHEN D1.SRECS_FILE_NM IS NOT NULL THEN D1.SRECS_FILE_NM||'.'||D1.FL_EXT_NM||' ('||REPLACE(TO_CHAR(D1.FL_SZ_TXT,'999,999'),' ','')||'bytes)' ELSE '' END AS SRECS_FILE_NM /* 파일명 */,
    D1.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBFAAAEA009M D1 LEFT OUTER JOIN TBFAAAEA001M D2 ON D1.SYS_NM = D2.SYS_NM AND D1.JRSD_INST_CD = D2.JRSD_INST_CD WHERE 1=1 AND D2.USE_YN = 'Y' AND D1.SYS_NM = #strSysNm# AND D2.BAI_ORG_CD = #strCoptOrgCd# ORDER BY D1.FRS_MDM_SQNO