SELECT D1.DOC_NM /* 문서명 */,
    (SELECT SUBSTR(REPLACE(REPLACE(XMLAGG(XMLELEMENT(A,', '||SRECS_FILE_NM||'.'||FL_EXT_NM)),' ',''),' ',''),2) AS SRECS_FILE_NM /* 파일명 */
FROM TBFAAAEA009M WHERE SYS_NM = #strSysNm# AND JRSD_INST_CD = #JRSD_INST_CD# AND DOC_NM = D1.DOC_NM) AS FL_NM_EXT FROM TBFAAAEA009M D1 WHERE SYS_NM = #strSysNm# AND JRSD_INST_CD = #JRSD_INST_CD# GROUP BY DOC_NM