SELECT ADT_KND_NM /* 감사종류명 */,
    NVL(SUM(CASE WHEN ACADT_SECD = '1' THEN ADT_CPSC_NOPE ELSE 0 END ), 0) AS ACADT_SECD /* 실지감사구분코드 */,
    NVL(SUM(CASE WHEN ACADT_SECD = '1' THEN ADT_DCN ELSE 0 END), 0) AS ADT_DCN /* 감사일수 */,
    NVL(SUM(CASE WHEN ACADT_SECD = '1' THEN 1 ELSE 0 END), 0) AS ACADT_SECD /* 실지감사구분코드 */
FROM TBFAAAEA019M WHERE (INST_CD = #INST_CD# OR INST_CD IN (SELECT BFR_INST_CD /* 이전기관코드 */
FROM TBFAAAEA023C WHERE INST_CD = #INST_CD# ) ) WHERE ( INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' OR INST_CD IN (SELECT BFR_INST_CD /* 이전기관코드 */
FROM TBFAAAEA023C WHERE INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' ) ) AND CVLCPT_ADT_YR = #CVLCPT_ADT_YR# AND DEL_YN = 'N' GROUP BY ADT_KND_NM