SELECT CVLCPT_ADT_YR /* 민원감사년도 */,
    SUM(1) AS 1,
    SUM( CASE WHEN ACADT_SECD = '1' THEN 1 ELSE 0 END) AS ACADT_SECD /* 실지감사구분코드 */
FROM TBFAAAEA019M WHERE (INST_CD = #INST_CD# OR INST_CD IN (SELECT BFR_INST_CD /* 이전기관코드 */
FROM TBFAAAEA023C WHERE INST_CD = #INST_CD# ) ) AND CVLCPT_ADT_YR BETWEEN #strSAudYr# AND #strEAudYr# WHERE (INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' OR INST_CD IN (SELECT BFR_INST_CD /* 이전기관코드 */
FROM TBFAAAEA023C WHERE INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' ) ) AND CVLCPT_ADT_YR BETWEEN #strSAudYr# AND #strEAudYr# AND DEL_YN = 'N' GROUP BY CVLCPT_ADT_YR ORDER BY CVLCPT_ADT_YR DESC