SELECT F_CODE_NM('1000047',D1.TPOF_KDCD) AS F_CODE_NM,
    D1.TPOF_RCPT_NO /* 제보접수번호 */,
    '' AS CVPT_DL,
    D1.TPOF_NM /* 제보명 */,
    F_CODE_NM('1000361',D1.TPOF_STTS_SECD) AS PRCS_NM /* 처리명 */,
    F_CODE_NM('1000051',D2.TPOF_PRCS_SECD) AS F_CODE_NM,
    F_USR_INFO_BY_ID(D3.TPOF_PRCS_PIC_ID,'2') AS SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */
FROM TB_CSPDCP101M D1 LEFT OUTER JOIN TB_CSPDCP205L D2 ON D1.CVLCPT_CLM_SECD = D2.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D2.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D2.TPOF_RCPT_NO AND D2.TPOF_SQNO = '1' LEFT OUTER JOIN TB_CSPDCP201M D3 ON D1.CVLCPT_CLM_SECD = D3.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D3.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D3.TPOF_RCPT_NO INNER JOIN TB_CSPDCP206L D4 ON D4.CVLCPT_CLM_SECD = '1' AND D4.TPOF_INST_DSCTN_SECD = '1' AND D1.CVLCPT_CLM_SECD = D4.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D4.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D4.TPOF_RCPT_NO WHERE D4.TPOF_INST_CD = #INST_CD# WHERE D4.TPOF_INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' WHERE D4.TPOF_INST_CD = #INST_CD# WHERE D4.TPOF_INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' ORDER BY CVPT_KND_NM, D1.TPOF_RCPT_NO