SELECT D1.ADT_YR||'-'||D1.ADT_NO AS ADT_YR /* 감사년도 */,
    D1.DSPS_RQT_SQNO /* 처분요구순번 */,
    CASE WHEN D2.CMO_INST_CD IS NOT NULL THEN F_ORG_HRK_NM(D2.CMO_INST_CD) ELSE '' END AS CMO_INST_CD /* 소관청기관코드 */,
    F_CODE_NM('1000002',D1.DSPS_RQT_KDCD) AS DSPS_RQT_KND_CVN /* 처분요구종류코드값명 */,
    D1.DSPS_RQT_NM /* 처분요구명 */,
    CASE WHEN PRCS_TERM_YMD IS NOT NULL THEN SUBSTRING(PRCS_TERM_YMD,0,4)||'.'||SUBSTRING(PRCS_TERM_YMD,5,2)||'.'||SUBSTRING(PRCS_TERM_YMD,7,2) ELSE PRCS_TERM_YMD END AS PRCS_TERM_YMD /* 처리기한일자 */
FROM TB_BADEAR001M D1 INNER JOIN TB_BADEAR002D D2 ON D1.ADT_YR = D2.ADT_YR AND D1.ADT_NO = D2.ADT_NO AND D1.DSPS_RQT_SQNO = D2.DSPS_RQT_SQNO WHERE D2.CMO_INST_CD = #CMO_INST_CD# WHERE D2.CMO_INST_CD LIKE REPLACE(#CMO_INST_CD#,'000','') || '%' WHERE D2.CMO_INST_CD = #CMO_INST_CD# WHERE D2.CMO_INST_CD LIKE REPLACE(#CMO_INST_CD#,'000','') || '%' AND D1.DSPS_RQT_NM LIKE '%'||#strTitTxt#||'%' ORDER BY D1.ADT_YR DESC, D1.ADT_NO DESC, D1.DSPS_RQT_SQNO ASC