SELECT CASE WHEN D1.MTRL_KND_CD = '2' THEN D1.INF_NO ELSE F_CODE_NM('1000401',D1.MTRL_KND_CD) END AS MTRL_KND_CD,
    D2.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    D1.MTRL_NM,
    D1.ETRT_NOC,
    CASE WHEN D1.ETRT_STAD_DT IS NOT NULL THEN SUBSTRING(D1.ETRT_STAD_DT,0,4)||'.'||SUBSTRING(D1.ETRT_STAD_DT,5,2)||'.'||SUBSTRING(D1.ETRT_STAD_DT,7,2) ELSE D1.ETRT_STAD_DT END AS ETRT_STAD_DT,
    D1.ETRT_TG_PRD_TXT
FROM TBCSPBII800L D1 LEFT OUTER JOIN TBDCMACM015M D2 ON D2.INST_CD = D1.JRSD_MAO_SECD WHERE 1=1 AND D1.JRSD_MAO_SECD = #JRSD_MAO_SECD# AND D1.JRSD_MAO_SECD LIKE REPLACE(#JRSD_MAO_SECD#,'000','') || '%' AND D1.JRSD_MAO_SECD = #JRSD_MAO_SECD# AND D1.JRSD_MAO_SECD LIKE REPLACE(#JRSD_MAO_SECD#,'000','') || '%' ORDER BY ETRT_STAD_DT DESC