SELECT INST_CD /* 기관코드 */,
    FRS_MDM_SQNO /* 포렌식매체순번 */,
    INFO_RLPR_NM /* 정보관계자명 */,
    CASE WHEN DSPS_RQT_TRPR_BRDT IS NOT NULL THEN SUBSTRING(DSPS_RQT_TRPR_BRDT,0,4)||'.'||SUBSTRING(DSPS_RQT_TRPR_BRDT,5,2)||'.'||SUBSTRING(DSPS_RQT_TRPR_BRDT,7,2) ELSE DSPS_RQT_TRPR_BRDT END AS DSPS_RQT_TRPR_BRDT /* 처분요구대상자생년월일 */,
    (CASE WHEN RPIM_BGNG_YMD IS NOT NULL THEN SUBSTRING(RPIM_BGNG_YMD,0,4)||'.'||SUBSTRING(RPIM_BGNG_YMD,5,2)||'.'||SUBSTRING(RPIM_BGNG_YMD,7,2) ELSE RPIM_BGNG_YMD END) ||' ~ '|| (CASE WHEN RPIM_EXPRY_YMD IS NOT NULL THEN SUBSTRING(RPIM_EXPRY_YMD,0,4)||'.'||SUBSTRING(RPIM_EXPRY_YMD,5,2)||'.'||SUBSTRING(RPIM_EXPRY_YMD,7,2) ELSE RPIM_EXPRY_YMD END) AS RPIM_BGNG_YMD /* 재임시작일자 */,
    RMRK_CN /* 비고내용 */
FROM TBFAAAEA015M WHERE INST_CD = #INST_CD# WHERE INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' ORDER BY FRS_MDM_SQNO DESC