SELECT NTV_ADDR /* 출신주소 */,
    CASE WHEN FRST_APNTMN_YMD IS NOT NULL THEN SUBSTRING(FRST_APNTMN_YMD,0,4)||'.'||SUBSTRING(FRST_APNTMN_YMD,5,2)||'.'||SUBSTRING(FRST_APNTMN_YMD,7,2) ELSE FRST_APNTMN_YMD END AS FRST_APNTMN_YMD /* 최초임용일자 */,
    CASE WHEN NOW_JBGD_APNTMN_YMD IS NOT NULL THEN SUBSTRING(NOW_JBGD_APNTMN_YMD,0,4)||'.'||SUBSTRING(NOW_JBGD_APNTMN_YMD,5,2)||'.'||SUBSTRING(NOW_JBGD_APNTMN_YMD,7,2) ELSE NOW_JBGD_APNTMN_YMD END AS NOW_JBGD_APNTMN_YMD /* 현재직급임용일자 */,
    MAIN_ACBG_CN /* 주요학력내용 */,
    MAIN_CRR_CN /* 주요경력내용 */
FROM TBFAAAEA016M WHERE INST_CD = #INST_CD# WHERE INST_CD LIKE REPLACE(#INST_CD#,'000','') || '%' AND FRS_MDM_SQNO = #FRS_MDM_SQNO#