SELECT D1.INFO_YR || '-' || D1.INFO_SQNO AS INFO_YR /* 정보년도 */,
    CASE WHEN D1.SBMSN_YMD IS NOT NULL THEN SUBSTRING(D1.SBMSN_YMD,0,4)||'.'||SUBSTRING(D1.SBMSN_YMD,5,2)||'.'||SUBSTRING(D1.SBMSN_YMD,7,2) ELSE D1.SBMSN_YMD END AS SBMSN_YMD /* 제출일자 */,
    D2.USER_NM AS USER_NM /* 사용자명 */,
    D1.INFO_NM /* 정보명 */,
    F_ORG_INFO(D1.REL_INST_CD, '1') AS F_ORG_INFO,
    D3.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    F_CODE_NM('1000039', D1.PRCS_DRCTN_SECD) AS F_CODE_NM,
    F_CODE_NM('1000085', D1.ADT_INFO_PRGRS_STTS_SECD) AS F_CODE_NM,
    CASE WHEN D1.GPCD_PRCS_YMD IS NOT NULL THEN SUBSTRING(D1.GPCD_PRCS_YMD,0,4)||'.'||SUBSTRING(D1.GPCD_PRCS_YMD,5,2)||'.'||SUBSTRING(D1.GPCD_PRCS_YMD,7,2) ELSE D1.GPCD_PRCS_YMD END AS GPCD_PRCS_YMD /* 처리일자 */,
    F_CODE_NM('1000042', D1.PRCS_RSLT_SECD) AS F_CODE_NM
FROM TB_CSPBII100M D1 LEFT OUTER JOIN TBDCMACM001M D2 ON D1.PRSNR_PBADMS_STD_SECD = D2.TRGT_NOP_ENO LEFT OUTER JOIN TBDCMACM002M D3 ON D1.ADT_DEPT_CD = D3.SRECS_DEPT_CD WHERE D1.REL_INST_CD = #REL_INST_CD# WHERE D1.REL_INST_CD LIKE REPLACE(#REL_INST_CD#,'000','') || '%' WHERE D1.REL_INST_CD = #REL_INST_CD# WHERE D1.REL_INST_CD LIKE REPLACE(#REL_INST_CD#,'000','') || '%' ORDER BY D1.SBMSN_YMD DESC, D1.INFO_YR DESC, D1.INFO_SQNO DESC