SELECT A.INFO_YR || '-' || LPAD(A.FRS_MDM_SQNO, 5, '0') AS INFO_YR /* 정보년도 */,
    B.INFO_RLPR_NM /* 정보관계자명 */,
    '******-*******' AS RRN_MASK /* 모든 사용자 주민번호 비공개 */,
    (SELECT B.PROF_RBPRSN_INST_NM AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBDCMACM015M B WHERE B.INST_CD = A.OGDP_INST_CD) AS TPOF_OGDP_INST_NM , A.JBPS_NM , F_CODE_NM('1000070', A.EVL_MTRL_DVSN_CD) AS EVL_MTRL_DVSN_NM , (SELECT B.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M B WHERE B.SRECS_DEPT_CD = A.EVL_DPT_CD) AS EVL_DPT_NM /* 평가부서명 */ , A.EVL_DT , CASE B.RSG_YN WHEN 'Y' THEN B.RSG_YN ELSE '' END AS RSG_YN , A.INFO_YR , A.FRS_MDM_SQNO , A.CRU_RRNO_SCRTY FROM TBCSPBII500L A /* 직무역량평가내역 */ , TBCSPBII300M B /* 대인정보기본 */ WHERE A.CRU_RRNO_SCRTY = B.CRU_RRNO_SCRTY AND A.OGDP_INST_CD = #OGDP_INST_CD#