SELECT D2.QSTNAI_YR||'-'||D2.QSTNAI_NO||'-'||D2.DTIL_ANS_NO||'-'||D2.DTIL_DVSN_NO AS QSTNAI_YR /* 세부질의번호 */,
    D2.QSTNAI_YR AS QSTNAI_YR /* 질의서년도 */,
    D2.QSTNAI_NO AS QSTNAI_NO /* 질의서번호 */,
    D2.DTIL_ANS_NO AS DTIL_ANS_NO /* 세부답변번호 */,
    D2.DTIL_DVSN_NO AS DTIL_DVSN_NO /* 세부구분번호 */,
    D2.PIC_NM AS PIC_NM /* 담당자명 */,
    D2.PIC_ID AS PIC_ID /* 담당자아이디 */,
    D2.CHR_DPT_CD AS CHR_DPT_CD /* 담당부서코드 */,
    D2.TKCG_DEPT_NM AS TKCG_DEPT_NM /* 담당부서명 */,
    D2.QUE_TXT AS QUE_TXT /* 질의내용 */,
    D2.SBMSN_YMD AS SBMSN_YMD /* 제출일자 */,
    (SELECT Z1.NTAS_SMT_DDLN_DT
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS NTAS_SMT_DDLN_DT /*국회제출기한*/ , DECODE(D2.NTAS_RQ_MTRL_HNDL_STA_CD, '20', '30', D2.NTAS_RQ_MTRL_HNDL_STA_CD) AS NTAS_RQ_MTRL_HNDL_STA_CD /*처리상태코드*/ , F_CODE_NM('1000374', DECODE(D2.NTAS_RQ_MTRL_HNDL_STA_CD, '20', '30', D2.NTAS_RQ_MTRL_HNDL_STA_CD)) AS NTAS_RQ_MTRL_HNDL_STA_NM /*처리상태명*/ , (SELECT Z1.SRECS_SRNG_MBCMT_NM /* 심사재심의심사위원명 */
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS SRECS_SRNG_MBCMT_NM /*위원명*/ , (SELECT Z1.RQT_YMD /* 요구일자 */
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS RQT_YMD /*요청일자*/ , (SELECT Z1.SMT_RQ_DT
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS SMT_RQ_DT /*제출일자*/ , (SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B2') AS ETC_DOC_ID , (SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B1') AS HC_DOC_ID FROM TBCSPFPL002M D2 WHERE 1=1 AND D2.CHR_DPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, '', #strDptCd#))) AND D2.CHR_DPT_CD = #strDptCd2# AND D2.QUE_TXT LIKE '%'||#strTxt#||'%'