/* faa/cav/001/FAACAV001ECodeXMainselect */ SELECT ROWNUM,
    A.JRSD_CD||'-'||A.ACNTG_CD||'-'||A.BLL_KDCD||'-'||A.PROF_RBPRSN_SQNO||'-'|| A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS JRSD_CD /* 소관코드 */,
    /*증명책임자부호*/ A.PROF_YM AS PROF_YM /* 증명연월 */,
    /* 증명년월 */ A.BLL_RCPTN_DT AS BLL_RCPTN_DT /* 계산서수신일시 */,
    /* 수신일자 */ A.BLL_RCPTN_STTS_SECD AS BLL_RCPTN_STTS_SECD /* 계산서수신상태구분코드 */,
    /* 수신상태 */ A.PROF_RBPRSN_FRLN_GVMNFC_SECD AS PROF_RBPRSN_FRLN_GVMNFC_SECD /* 증명책임자일선관서구분코드 */,
    /* 관서부호 */ F_AV_ACCMN_NM(A.JRSD_CD,A.ACNTG_CD,A.BLL_KDCD ,A.PROF_RBPRSN_SQNO ,A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    /* 기관명 */ A.BLL_BKUP_PATH_NM AS BLL_BKUP_PATH_NM /* 계산서백업경로명 */,
    F_AV_BLL_COPT_CD_NM(A.JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(A.ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    F_AV_BLL_CD_NM(A.BLL_KDCD, A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS F_AV_BLL_CD_NM,
    '' AS PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */,
    '' AS CRU_TELNO /* 부패전화번호 */,
    '' AS PROF_RBPRSN_MDFR_NM /* 증명책임자수정자명 */
FROM TWFAACAV002H A WHERE 1=1 AND A.PROF_YM = #PROF_YM# AND A.PROF_YM LIKE #PROF_YM#||'%' AND A.JRSD_CD = #JRSD_CD# AND A.ACNTG_CD = #ACNTG_CD# AND (A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO#) AND (A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO#) AND A.BLL_KDCD = #BLL_KDCD# AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND A.BLL_KDCD IN ('17','18') AND A.BLL_KDCD IN ('22','23','24') AND A.BLL_KDCD IN ('42','43') AND A.BLL_KDCD = CASE WHEN #BLL_KDCD# IN ('17','22','42') THEN A.BLL_KDCD ELSE #BLL_KDCD# END AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND A.BLL_KDCD IN ('17','18') AND A.BLL_KDCD IN ('22','23','24') AND A.BLL_KDCD = CASE WHEN #BLL_KDCD# IN ('17','22') THEN A.BLL_KDCD ELSE #BLL_KDCD# END AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND A.PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND A.PROF_RBPRSN_FRLN_GVMNFC_SECD like '%'||#strFildGovCd#||'%' AND A.BLL_RCPTN_DT BETWEEN TO_TIMESTAMP( #strSdt#,'YYYYMMDD') AND TO_TIMESTAMP(#strEdt#||235959,'YYYYMMDDHH24MISS') AND A.BLL_RCPTN_DT BETWEEN TO_TIMESTAMP(#strSdt#,'YYYYMMDD') AND TO_TIMESTAMP(SYSDATE,'YYYYMMDDHH24MISS') AND A.BLL_RCPTN_DT &lt;= TO_TIMESTAMP(#strEdt#||235959,'YYYYMMDDHH24MISS') AND A.BLL_RCPTN_STTS_SECD = #BLL_RCPTN_STTS_SECD# ORDER BY PROF_DRTR_CD, PROF_YM, RCPTN_DT