/* faa/cav/001/FAACAV001EMainDtlselect */ SELECT ERRDTL.PROF_YM /* 증명연월 */,
    TO_CHAR(ERRDTL.BLL_RCPT_DT, 'YYYY-MM-DD HH24:MM:SS') AS RCPTN_DT /* 수신일시 */,
    ERRDTL.BLL_RCPT_RCPT_ERR_SQNO /* 계산서접수접수오류순번 */,
    ERRDTL.BLL_RCPT_ERR_SECD /* 계산서접수오류구분코드 */,
    CD.RMRK_DTL_CN /* 비고상세내용 */
FROM TWFAACAV006D ERRDTL, TWFAACAV024C CD WHERE ERRDTL.JRSD_CD||ERRDTL.ACNTG_CD||ERRDTL.BLL_KDCD||ERRDTL.PROF_RBPRSN_SQNO||ERRDTL.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = REPLACE(#strProfDrtrCd#,'-','') AND ERRDTL.PROF_YM = #PROF_YM# AND ERRDTL.BLL_RCPT_ERR_SECD = CD.ACNTG_CD AND CD.ACNTG_SECD = '11' AND ERRDTL.BLL_RCPT_DT = #CRU_RCPT_DT#