/* faa/cav/001/FAACAV001R_01 */ SELECT ERRDTL.JRSD_CD|| '-' ||ERRDTL.ACNTG_CD|| '-' ||ERRDTL.BLL_KDCD|| '-' ||ERRDTL.PROF_RBPRSN_SQNO|| '-' ||ERRDTL.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    SUBSTR(ERRDTL.PROF_YM,1,4) || '.' || SUBSTR(ERRDTL.PROF_YM,5,2) AS 증명년월 AS PROF_YM /* 증명연월 */,
    TO_CHAR(ERRDTL.BLL_RCPT_DT, 'YYYY.MM.DD. HH24:MI:SS') AS 접수일시 AS BLL_RCPT_DT /* 계산서접수일시 */,
    ERRDTL.BLL_RCPT_RCPT_ERR_SQNO AS 일련번호 AS BLL_RCPT_RCPT_ERR_SQNO /* 계산서접수접수오류순번 */,
    ERRDTL.BLL_RCPT_ERR_SECD AS 접수오류코드 AS BLL_RCPT_ERR_SECD /* 계산서접수오류구분코드 */,
    CD.RMRK_DTL_CN AS 비고 AS RMRK_DTL_CN /* 비고상세내용 */
