/* faa/cav/003/FAACAV003EMainselectDtl */ SELECT ROWNUM,
    A.JRSD_CD||'-'||A.ACNTG_CD||'-'||A.BLL_KDCD||'-'||A.PROF_RBPRSN_SQNO||'-'|| A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS JRSD_CD /* 소관코드 */,
    A.PROF_YM /* 증명연월 */,
    A.BLL_RCPT_DT AS BLL_RCPT_DT /* 계산서접수일시 */,
    B.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    F_AV_BLL_COPT_CD_NM(A.JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(A.ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    F_AV_BLL_CD_NM(A.BLL_KDCD, A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS F_AV_BLL_CD_NM,
    C.BLL_PRCS_STTS_TPCD /* 계산서처리상태유형코드 */,
    D.PRCS_STTS_YN /* 처리상태여부 */,
    A.JRSD_CD /* 소관코드 */,
    A.ACNTG_CD /* 회계코드 */,
    A.BLL_KDCD /* 계산서종류코드 */,
    A.PROF_RBPRSN_SQNO /* 증명책임자순번 */,
    A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO /* 관서운영경비출납공무원순번 */,
    B.PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */,
    B.PROF_RBPRSN_TELNO /* 증명책임자전화번호 */,
    B.PROF_RBPRSN_MDFR_NM /* 증명책임자수정자명 */
FROM TWFAACAV005M A , TWFAACAV003M B LEFT OUTER JOIN TWFAACAV001M C ON ( C.JRSD_CD = B.JRSD_CD AND C.ACNTG_CD = B.ACNTG_CD AND C.BLL_KDCD = B.BLL_KDCD AND C.PROF_RBPRSN_SQNO = B.PROF_RBPRSN_SQNO AND C.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = B.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) LEFT OUTER JOIN TWFAACAV016M D ON ( D.JRSD_CD = B.JRSD_CD AND D.ACNTG_CD = B.ACNTG_CD AND D.BLL_KDCD = B.BLL_KDCD AND D.PROF_RBPRSN_SQNO = B.PROF_RBPRSN_SQNO AND D.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = B.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) WHERE 1 = 1 AND A.JRSD_CD = B.JRSD_CD AND A.ACNTG_CD = B.ACNTG_CD AND A.BLL_KDCD = B.BLL_KDCD AND A.PROF_RBPRSN_SQNO = B.PROF_RBPRSN_SQNO AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = B.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND A.BLL_RCPT_ERR_PRCS_SECD = 'W' /*증명월이 있으면 */ AND A.PROF_YM = #PROF_YM# /*증명월이 없으면 */ AND A.PROF_YM BETWEEN #strProfYr#||'00' AND #strProfYr#||'13' AND A.JRSD_CD = #JRSD_CD# AND A.ACNTG_CD = #ACNTG_CD# AND A.BLL_KDCD IN('17','18') AND A.BLL_KDCD IN('22','23','24') AND A.BLL_KDCD IN('42','43') AND A.BLL_KDCD =#BLL_KDCD# AND A.PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO != '000' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' ORDER BY KEY,PROF_DRTR_CD,PROF_YM, RCPTN_DT